import React, { Component } from 'react';
import styled from "@emotion/styled";

import ScoreBoardAnimate from '../assets/scoreBoardAnimate.js';

const ScoreBoard = styled.div`
	display: grid;
	grid-template-columns: 50% 50%;
	grid-template-rows: repeat(4, auto);
  font-family: sans-serif;


  .title{
    grid-column-start: 1;
    grid-column-end: 3;
    text-align: center;
  }

  .name{
    font-size: 30px;
    grid-row-start: 1;
    grid-row-end: 2;
  }

	.team1, .team2 {
		grid-column-start: 0;
		width: 100%;
		text-align: center;
    grid-row-start: 2;

    .score{
      font-family: sans-serif;
      font-size: 100px;
      margin-top: 10px;
      margin-bottom: 30px;
    }

		.scoreButtons {
      width: 100%;
      display: grid;
      grid-template-columns: repeat(5, 19.5%);
      grid-template-rows: repeat(3, 80px);
      grid-gap: 3px;
      margin: 0 auto;

      .scoreButton{
        padding: 0;
        grid-column-start: 2;
        grid-column-end: 5;
      }
		}
	}
`;

const ResetButton = styled.button`
  font-family: sans-serif;
  height: 40px;
  width: 120px;
  display: block;
  margin: 60px auto 0 auto;
`;

function ScoreAnimate() {
  document.getElementById('TeamOneScore').classList.add('bucket');
  setTimeout(() => {
    document.getElementById('TeamOneScore').classList.remove('bucket');
  }, 1500);
}


// const T2ScoreBtn = document.getElementsByClassName('Team2Score');


class Scoreboard extends Component {
  state = {
    T1SCORE: 0,
    T2SCORE: 0
  }

  TeamOne3 = () => {
    this.setState({ T1SCORE: this.state.T1SCORE + 3 })
  }
  TeamOne2 = () => {
    this.setState({ T1SCORE: this.state.T1SCORE + 2 })
  }
  TeamOne1 = () => {
    this.setState({ T1SCORE: this.state.T1SCORE + 1 })
  }
  TeamTwo3 = () => {
    this.setState({ T2SCORE: this.state.T2SCORE + 3 })
  }
  TeamTwo2 = () => {
    this.setState({ T2SCORE: this.state.T2SCORE + 2 })
  }
  TeamTwo1 = () => {
    this.setState({ T2SCORE: this.state.T2SCORE + 1 });
    document.getElementsByClassName('Team1Score').addEventListener('click', ScoreAnimate);
  }

  Reset = () => {
    this.setState({
      T1SCORE: 0,
      T2SCORE: 0
    })
  }

  render() {
    return (
      <>
        <ScoreBoard>
          <div className="team1">
            <h3 className="name">Player 1</h3>
            <ScoreBoardAnimate>
              <h2 className="score" id="TeamOneScore"> {this.state.T1SCORE} </h2>
            </ScoreBoardAnimate>

            <div className="scoreButtons">
              <button className="Team1Score" id="Team1Three" type="button" onClick={this.TeamOne3}>3</button>
              <button className="Team1Score" type="button" onClick={this.TeamOne2}>2</button>
              <button className="Team1Score" type="button" onClick={this.TeamOne1}>1</button>
            </div>
          </div>

          <div className="team2">
            <h3 className="name">Player 2</h3>
            <ScoreBoardAnimate>
              <h2 className="score" id="TeamTwoScore"> {this.state.T2SCORE} </h2>
            </ScoreBoardAnimate>

            <div className="scoreButtons">
              <button className="Team2Score" type="button" onClick={this.TeamTwo3}>3</button>
              <button className="Team2Score" type="button" onClick={this.TeamTwo2}>2</button>
              <button className="Team2Score" type="button" onClick={this.TeamTwo1}>1</button>
            </div>
          </div>
        </ScoreBoard>

        <ResetButton onClick={this.Reset}>Reset</ResetButton>
      </>
    );
  }
}

export default Scoreboard;
