import React, { Component } from 'react';
import styled from "@emotion/styled";

// import AddPoints from '../lib/addPoints';

const ScoreBoard = styled.div`
	display: grid;
	grid-template-columns: 50% 50%;
	grid-template-rows: repeat(4, auto);
  font-family: sans-serif;


  .title{
    grid-column-start: 1;
    grid-column-end: 3;
    text-align: center;
  }

  .name{
    font-size: 30px;
    grid-row-start: 1;
    grid-row-end: 2;
  }

	.team1, .team2 {
		grid-column-start: 0;
		width: 100%;
		text-align: center;
    grid-row-start: 2;

    .score{
      font-family: sans-serif;
      font-size: 100px;
      margin-top: 10px;
      margin-bottom: 30px;
    }

		.scoreButtons {
      width: 100%;
      display: grid;
      grid-template-columns: repeat(5, 19.5%);
      grid-template-rows: repeat(3, 80px);
      grid-gap: 3px;
      margin: 0 auto;

      .scoreButton{
        padding: 0;
        grid-column-start: 2;
        grid-column-end: 5;
      }
		}
	}
`;

const ResetButton = styled.button`
  font-family: sans-serif;
  height: 40px;
  width: 120px;
  display: block;
  margin: 60px auto 0 auto;
`;


const TeamOne = 'TeamOne';
const TeamTwo = 'TeamTwo';
const THREE = 3;
const TWO = 2;
let T1SCORE = 0;
let TEAMTWOSCORE = 0;


function scoreReset() {
  T1SCORE = 0;
  document.getElementById('TeamOneScore').innerHTML = T1SCORE;
  TEAMTWOSCORE = 0;
  document.getElementById('TeamTwoScore').innerHTML = TEAMTWOSCORE;
}

TeamOne3 = () => {
  this.setState( { T1SCORE: this.state.T1SCORE + 3 })
}

class Scoreboard extends Component {
  state = {
    T1SCORE: 0,
    T2SCORE: 0
  }
  render() {
    return (
      <>
        <ScoreBoard>
          <div className="team1">
            <h3 className="name">Player 1</h3>
            <h2 className="score" id="TeamOneScore"> {this.state.T1SCORE} </h2>

            <div className="scoreButtons">
              <button className="scoreButton" type="button" onClick={this.TeamOne3}>3</button>
              <button className="scoreButton" type="button" onClick={console.log("hey")}>2</button>
              <button className="scoreButton" type="button" onClick={console.log("hey")}>1</button>
            </div>
          </div>

          <div className="team2">
            <h3 className="name">Player 2</h3>
            <h2 className="score" id="TeamTwoScore"> {TEAMTWOSCORE} </h2>

            <div className="scoreButtons">
              <button className="scoreButton" type="button" onClick={console.log("hey")}>3</button>
              <button className="scoreButton" type="button" onClick={console.log("hey")}>2</button>
              <button className="scoreButton" type="button" onClick={console.log("hey")}>1</button>
            </div>
          </div>
        </ScoreBoard>

        <ResetButton onClick={scoreReset}>Reset</ResetButton>
      </>
    );
  }
}

export default Scoreboard;
