import React, { Component } from 'react';
// import styled from "@emotion/styled";


class Scoreboard extends Component {
  render() {
    return (
      <>
        <ScoreBoard>
          <div className="team1">
            <h3 className="name">Player 1</h3>
            <h2 className="score" id="TeamOneScore"> {TEAMONESCORE} </h2>

            <div className="scoreButtons">
              <button className="scoreButton" type="button" onClick={teamOne3}>3</button>
              <button className="scoreButton" type="button" onClick={teamOne2}>2</button>
              <button className="scoreButton" type="button" onClick={teamOne1}>1</button>
            </div>
          </div>

          <div className="team2">
            <h3 className="name">Player 2</h3>
            <h2 className="score" id="TeamTwoScore"> {TEAMTWOSCORE} </h2>

            <div className="scoreButtons">
              <button className="scoreButton" type="button" onClick={teamTwo3}>3</button>
              <button className="scoreButton" type="button" onClick={teamTwo2}>2</button>
              <button className="scoreButton" type="button" onClick={teamTwo1}>1</button>
            </div>
          </div>
        </ScoreBoard>

        <ResetButton onClick={scoreReset}>Reset</ResetButton>
      </>
    );
  }
}

export default Scoreboard;
