import React, { Component } from 'react';
import styled from '@emotion/styled';
import ScoreBoardAnimate from '../assets/scoreBoardAnimate.js';

const ScoreBoard = styled.div`
	display: grid;
	grid-template-columns: 50% 50%;
	grid-template-rows: repeat(4, auto);
  font-family: sans-serif;

  .title{
    grid-column-start: 1;
    grid-column-end: 3;
    text-align: center;
  }

  .name{
    font-size: 30px;
    grid-row-start: 1;
    grid-row-end: 2;
  }

	.team1, .team2 {
		grid-column-start: 0;
		width: 100%;
		text-align: center;
    grid-row-start: 2;

    .score{
      font-weight: bold;
      font-family: sans-serif;
      font-size: 100px;
      margin-top: 10px;
      margin-bottom: 30px;
    }
		.scoreButtons {
      width: 100%;
      display: grid;
      grid-template-columns: repeat(5, 19.5%);
      grid-template-rows: repeat(3, 80px);
      grid-gap: 3px;
      margin-bottom: 40px;

      .Team1Score, .Team2Score{
        padding: 0;
        grid-column-start: 2;
        grid-column-end: 5;
        font-size: 36px;
      }
		}
	}
`;

const ResetButton = styled.button`
  font-family: sans-serif;
  height: 40px;
  width: 120px;
  display: block;
  margin: 0 auto;
  font-size: 20px;
`;

const Stats = styled.div`
  margin-top: 20px; 
  font-family: sans-serif;

  .Players{
    width: 300px;
    display: grid;
    grid-template-columns: 100%;
    grid-template-rows: 40px 40px;
    margin: 0 auto;

    .Player1, .Player2 {
      font-size: 20px;
      display: grid;
      grid-template-columns: 30% 70%;
      align-self: center;
      align-items: center;
      text-align: center;
    }
  }
`;

const PlayerStats = styled.div`
  display: grid;
  grid-template-columns: 50% 50%;
  text-align: center;

  @media(max-width: 450px){
    grid-template-columns: 100%;
  }
`;

const Head2Head = styled.div`
  text-align: center;
`;

const TeamOne = 'TeamOneScore';
const TeamTwo = 'TeamTwoScore';

function ScoreAnimate(TEAM) {
  document.getElementById(TEAM).classList.add('bucket');
  setTimeout(() => {
    document.getElementById(TEAM).classList.remove('bucket');
  }, 1500);
}
// THIS IS STATE!!!!
class Scoreboard extends Component {
  state = {
    P1NAME: 'Player 1',
    P2NAME: 'Player 2',
    T1SCORE: 0,
    T2SCORE: 0,
    P1BOX3: [],
    P1BOX2: [],
    P1BOX1: [],
    P2BOX3: [],
    P2BOX2: [],
    P2BOX1: [],
    MODE: 'edit',
    INPUT1: '',
    INPUT2: '',
  }
  // THIS IS STATE!!!


  TeamOne3 = () => {
    this.setState({ T1SCORE: this.state.T1SCORE + 3 });
    this.setState({ P1BOX3:[...this.state.P1BOX3, 3]});
    ScoreAnimate(TeamOne);
  }
  TeamOne2 = () => {
    this.setState({ T1SCORE: this.state.T1SCORE + 2 });
    this.setState({ P1BOX2: [...this.state.P1BOX2, 2] });
    ScoreAnimate(TeamOne);
  }
  TeamOne1 = () => {
    this.setState({ T1SCORE: this.state.T1SCORE + 1 });
    this.setState({ P1BOX1: [...this.state.P1BOX1, 1] });
    ScoreAnimate(TeamOne);
  }
  TeamTwo3 = () => {
    this.setState({ T2SCORE: this.state.T2SCORE + 3 });
    this.setState({ P2BOX3:[...this.state.P2BOX3, 3]});
    ScoreAnimate(TeamTwo);
  }
  TeamTwo2 = () => {
    this.setState({ T2SCORE: this.state.T2SCORE + 2 });
    this.setState({ P2BOX2: [...this.state.P2BOX2, 2] });
    ScoreAnimate(TeamTwo);
  }
  TeamTwo1 = () => {
    this.setState({ T2SCORE: this.state.T2SCORE + 1 });
    this.setState({ P2BOX1: [...this.state.P2BOX1, 1] });
    ScoreAnimate(TeamTwo);
  }

  Reset = () => {
    this.setState({
      P1NAME: 'Player 1',
      P2NAME: 'Player 2',
      T1SCORE: 0,
      T2SCORE: 0,
      P1BOX3: [],
      P1BOX3Perc: 0,
      P1BOX2: [],
      P1BOX3Perc: 0,
      P1BOX1: [],
      P1BOX3Perc: 0,
      P2BOX3: [],
      P1BOX3Perc: 0,
      P2BOX2: [],
      P1BOX3Perc: 0,
      P2BOX1: [],
      P1BOX3Perc: 0,
    })
  }

  P1Name = (e) => {
    this.setState({P1NAME: e.target.value});
  }
  // P2Name = (e) => {
  //   this.setState({P2NAME: e.target.value});
  // }

  handleSave = () => {
    this.setState({ P1NAME: this.state.INPUT1, MODE: 'view'});
  }
  handleEdit= () => {
    this.setState({ MODE: 'edit'});
  }

  renderInputField() {
    if(this.state.MODE === 'view') {
      return <div></div>;
    } else {
      return(
        <h3>
          <input 
            onChange={this.P1Name}
            value={this.state.P1NAME}
          />
        </h3>
      );
    }
  }

  renderButton(){
    if (this.state.mode === 'view') {
      return (
        <button onClick={this.handleEdit}>
          Edit
          </button>
      );
    } else {
      return (
        <button onClick={this.handleSave}>
          Save
          </button>
      );
    }
  }

  render() {
    return (
      <>
      <ScoreBoardAnimate> 
        <ScoreBoard>
          <div className="team1">
            <h3 className="name">{this.state.P1NAME}</h3>
              <ScoreBoardAnimate className="score" id="TeamOneScore"> {this.state.T1SCORE} </ScoreBoardAnimate>

            <div className="scoreButtons">
              <button className="Team1Score" type="button" onClick={this.TeamOne3}>3</button>
              <button className="Team1Score" type="button" onClick={this.TeamOne2}>2</button>
              <button className="Team1Score" type="button" onClick={this.TeamOne1}>1</button>
            </div>
          </div>

          <div className="team2">
            <h3 className="name">{this.state.P2NAME}</h3>
              <ScoreBoardAnimate className="score" id="TeamTwoScore"> {this.state.T2SCORE} </ScoreBoardAnimate>

            <div className="scoreButtons">
              <button className="Team2Score" type="button" onClick={this.TeamTwo3}>3</button>
              <button className="Team2Score" type="button" onClick={this.TeamTwo2}>2</button>
              <button className="Team2Score" type="button" onClick={this.TeamTwo1}>1</button>
            </div>
          </div>

        </ScoreBoard>
        <ResetButton type="button" onClick={this.Reset}>Reset</ResetButton>
      </ScoreBoardAnimate>

      <Stats>

        <PlayerStats>
          <div className="P1Stats">
            <h2>{this.state.P1NAME}'s Point Breakdown</h2>
            <h2>3 Pointers: {this.state.P1BOX3.length}</h2>
            <h2>2 Pointers: {this.state.P1BOX2.length}</h2>
            <h2>1 Pointers: {this.state.P1BOX1.length}</h2>
          </div>
          <div className="P2Stats">
            <h2>{this.state.P2NAME}'s Point Breakdown</h2>
            <h2>3 Pointers: {this.state.P2BOX3.length}</h2>
            <h2>2 Pointers: {this.state.P2BOX2.length}</h2>
            <h2>1 Pointers: {this.state.P2BOX1.length}</h2>
          </div>
        </PlayerStats>
        <Head2Head>
          <h2>{this.state.P1NAME} VS {this.state.P2NAME}</h2>
          <h3>3 Pointers: {this.state.P1BOX3.length} : {this.state.P2BOX3.length}</h3>
          <h3>2 Pointers: {this.state.P1BOX2.length} : {this.state.P2BOX2.length}</h3>
          <h3>1 Pointers: {this.state.P1BOX1.length} : {this.state.P2BOX1.length}</h3>
        </Head2Head>

          <div className="Players">
            <div className="Player1">
              <h3>{this.state.P1NAME}</h3>
              {this.renderInputField()}
              {this.renderButton()}
            </div>

          </div>

      </Stats>
      </>
    );
  }
}

export default Scoreboard;

